import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-app';
}
<h1>ToolCrib Self Checkout</h1>
<div class="center-div">
  <div style="max-height: 256px; max-width: 512px; overflow: scroll;">
    <canvas height="512px" width="812px"></canvas>
  </div>
</div>
