import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-app';
}
<head>
  <style>
  h1 {
    position: absolute;
    left: 400px;
    top: 150px;
  }
  </style>
  </head>

  <body>

    <h1>The position Property</h1>

    <h2>This is a heading with an absolute position</h2>

    <p>With absolute positioning, an element can be placed anywhere on a page. The heading below is placed 100px from the left of the page and 150px from the top of the page.</p>

    </body>

<div class="center-div">
  <div style="max-height: 256px; max-width: 256px; overflow: scroll;">
    <canvas height="512px" width="512px"></canvas>
  </div>
</div>
